import "./App.css";
//import Item from "./MyItem";
import React from "react";
class StarWars extends React.Component {
  constructor() {
    super();
    this.state = {
      name: null,
      height: null,
      homeworld: null,
      films: [],
    };
  }
  getNewCharacter() {
    console.log("from button");
    this.setState({
      name: "Luke",
      height: 172,
      homeworld: "Tatooine",
      films: ["Item 1", "Item 2"],
    });
  }
  render() {
    return (
      <div>
        {this.state.loadedCharacter && (
          <div>
            <h1>{this.state.name}</h1>
            <p>{this.state.height} cm</p>
            <p>Homeworld: {this.state.homeworld}</p>
            <ul>
              <li>Films in here</li>
            </ul>
          </div>
        )}

        <button
          type="button"
          onClick={() => this.getNewCharacter()}
          className="btn"
        >
          Randomize Character
        </button>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <StarWars />
      </header>
    </div>
  );
}

export default App;
